import React from 'react' 
import Value from './Value'
import Countor from './Countor'
import { connect  } from 'react-redux';

let Cotrol = () => {
    return (
        <div>
            <Value number={this.props.number}/>
        </div>
    )
}

let mapStateToProp = (state) => {
    return {
        number : state.number,
        color : state.color
    }
}

let mapDispatchToProp = (dispatch) =>{
    return {
        handleIncrement : ()=> dispatch.increment(),
        handleDecrement : ()=> dispatch.decrement(),
        handleChangeColor : (color)=> dispatch.setColor(color)
    }
}




export default connect(mapStateToProp,mapDispatchToProp)(Cotrol);
